/*
 * This file was generated by the Gradle 'init' task.
 *
 * This project uses @Incubating APIs which are subject to change.
 */

plugins {
    antlr
    `java-library`
    `maven-publish`
}

repositories {
    mavenLocal()
    maven {
        url = uri("https://repo.maven.apache.org/maven2/")
    }
}

dependencies {
    antlr("org.antlr:antlr4:4.13.2")
    implementation("org.antlr:antlr4-runtime:4.13.2")
    testImplementation(libs.junit.junit)
}

group = "cn.edu.sjtu.songyuke"
version = "1.0-SNAPSHOT"
description = "mental"
java.sourceCompatibility = JavaVersion.VERSION_21

publishing {
    publications.create<MavenPublication>("maven") {
        from(components["java"])
    }
}

tasks.withType<JavaCompile>() {
    options.encoding = "UTF-8"
}

tasks.withType<Javadoc>() {
    options.encoding = "UTF-8"
}

tasks.compileJava {
    dependsOn(tasks.generateGrammarSource)
}

tasks.generateGrammarSource {
    maxHeapSize = "64m"
    outputDirectory = file(layout.buildDirectory.dir("generated-src/antlr/main/cn/edu/sjtu/songyuke/mental/antlr4"))
    arguments = arguments + listOf("-visitor", "-long-messages", "-package", "cn.edu.sjtu.songyuke.mental.antlr4")
}
